import express from 'express';
import { getAllClubs, createClub, updateClub, deleteClub } from '../controllers/club.controller';  
import { PrismaClient } from '@prisma/client';
const router = express.Router();
const prisma = new PrismaClient();


router.get('/api/clubs', getAllClubs);          // GET all franchises
router.post('/api/clubs', createClub);          // POST new franchise
router.put('/api/clubs/:id', updateClub);       // PUT update franchise
router.delete('/api/clubs/:id', deleteClub);    // DELETE franchise

export default router;
